import React, {useEffect, useState} from "react";
import {Link, useParams, useHistory} from "react-router-dom";
import {readDeck, deleteDeck, deleteCard} from "../../utils/api/index";


function Deck () {
  const [deck, setDeck] = useState ({cards:[]})
  const { deckId } = useParams();
  const history = useHistory();

  useEffect(() => {
    async function fetchData() {
        const abortController = new AbortController();
        try {
            const deckResponse = await readDeck(deckId, abortController.signal);
            setDeck(deckResponse);
        } catch (error) {
            console.error("Something went wrong", error);
        }
        return () => {
            abortController.abort();
        };
    }
    fetchData();
}, [deckId]);

function handleDeleteDeck(deck) {

  if (
      window.confirm(
          `Delete this deck? You will not be able to recover it`
      )
  ) {
      history.go(0);
      return deleteDeck(deck.id);
  }
}

function handleDeleteCard(card) {

  if (
      window.confirm(
          `Delete this card? You will not be able to recover it`
      )
  ) {
      history.go(0);
      return deleteCard(card.id);
  }
}

  return (
   <div>
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
        <li className="breadcrumb-item active" aria-current="page">
        <Link to='/'>Home</Link>
        </li>
        <li className="breadcrumb-item" aria-current="page">{deck.name}</li>
        </ol>
      </nav>

    <div key={deck.id} className="card-deck">
    <div className="card-body">
    <h5 className="card-title">{deck.name}</h5>
    <p className="card-text">{deck.description}</p>
    <Link to={`/decks/${deck.id}/edit`} className="btn btn-secondary m-lg-1">Edit</Link>
    <Link to={`/decks/${deck.id}/study`} className="btn btn-primary m-lg-1">Study</Link>
    <Link to={`/decks/${deck.id}/cards/new`} className="btn btn-primary m-lg-1">Add Cards</Link>
    <button type="button" className="btn btn-danger" onClick={() => handleDeleteDeck(deck)}>Delete</button>
    </div>
  </div>

  {deck.cards.map(card => 
  <div key={card.id} className="card">
  <div className="row">
    <div className="col"> {card.front}
  </div>

  <div className="col"> {card.back} 
  <Link to={`/decks/${deck.id}/edit`} className="btn btn-secondary">Edit</Link>
  <button type="button" className="btn btn-danger" onClick={() => handleDeleteCard(card)}>Delete</button>
  </div>
  </div>
  </div>)}
</div> )
}
export default Deck
