import React, {useEffect, useState} from "react";
import {listDecks, deleteDeck} from "../utils/api/index";
import {Link, useHistory} from "react-router-dom";

function Home () {
  const [decks, setDecks] = useState ([])  
  const history = useHistory();

  useEffect(() => {
    async function fetchData() {
        const abortController = new AbortController();
        try {
            const deckResponse = await listDecks(abortController.signal);
            setDecks(deckResponse);
        } catch (error) {
            console.error("Something went wrong", error);
        }
        return () => {
            abortController.abort();
        };
    }
    fetchData();
}, []);

  function handleDelete(deck) {

    if (
        window.confirm(
            `Delete this deck? You will not be able to recover it`
        )
    ) {
        history.go(0);
        return deleteDeck(deck.id);
    }
}

  return (
   <div>
   <Link to="/decks/new" className="btn btn-secondary">Create Deck</Link>
     {decks.map(deck =>  <div key={deck.id} className="card">
    <div className="card-body">
    <h5 className="card-title">{deck.name}

    <small text-end="true">
         {deck.cards.length} cards
    </small>
    </h5>
    <p className="card-text">{deck.description}</p>
    <Link to={`/decks/${deck.id}`} className="btn btn-secondary">View</Link>
    <Link to={`/decks/${deck.id}/study`} className="btn btn-primary">Study</Link>
    <button type="button" className="btn btn-danger" onClick={() => handleDelete(deck)}>Delete</button>
    </div>

  </div>)}
</div> )
}

export default Home
